import React, { Component } from 'react';
// import logo from './logo.svg';
import PictureCard from './components/PictureCard';
import picture from './pics.json';

// generatin random
function generateRandom(array) {
  for (var i = array.length - 1; 1 > 0; i--) {
    var j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  };
  return array;
};


class App extends Component {
  state = {
    picture,
    clickedPicture: [],
    score: 0,
    progress: 0
  };

  clickedPic = id => {
    var clickedPicture = this.state.clickedPicture;
    var score = this.state.score;
    this.setState({
      showProgress: 0
    });

    // if the clicked image has an id of the indexed paintings
    if (clickedPicture.indexOf(id) === -1) {
      // push that id into that id into the array to be stored
      clickedPicture.push(id);
      console.log(clickedPicture);
      // run the score function
      this.handleIncrement();
      // run the reshuffle function after each click
      this.makeShuffle();
    } else if (this.state.score === 12) {
      // alert player wins
      this.setState({
        progress: 1,
        score: 0,
        clickedPicture: []
      });
    } else {
      // alert player loss
      this.setState({
        score: 0,
        clickedPicture: []
      });
      this.setState({
        showProgress: 1
      });
    };
  };

    // handleIncrement increases this.state.score by 1
    handleIncrement = () => {
      // setState updates a components states
      this.setState({ score: this.state.score + 1 });
    };



  // shuffles images
  shuffleThem = () => {
    // setState() enqueues changes to the component state and tells React that this component and its children need to be re-rendered with the updated state. This is the primary method you use to update the user interface in response to event handlers and server responses.  (react docs)
    this.setState({ picture: generateRandom(picture) })
  };

  render() {
    return (
      <div className="row">
        {this.state.picture.map(pics => (
          <PictureCard
            image={pics.image}
          />
        ))}
      </div>
    )
  };
};

export default App;
